mapper.select:
SELECT

FROM OUTSOURCE_ALLOCATION_REPAYMENT

	1=1

		AND CUST_ID = #{custId}


		AND IOUS = #{ious}


		AND COMPANY = #{company}


		AND REPAYMENT_DATE >= #{startRepaymentDateTime}


		AND REPAYMENT_DATE <= #{endRepaymentDateTime}

SELECT

FROM OUTSOURCE_ALLOCATION_REPAYMENT

	1=1

		AND COMPANY = #{company}


		AND REPAYMENT_DATE > #{startRepaymentDateTime}


		AND REPAYMENT_DATE < #{endRepaymentDateTime}

SELECT z.transfer AS transfer, z.hand_over_agecd AS handOverAgeCd, ROUND(SUM(z.hand_over_amount), 2) AS handOverAmount, ROUND(SUM(z.cur_amount), 2) AS curAmount FROM ( SELECT r.ious, r.transfer, r.hand_over_agecd, r.hand_over_amount, r.cur_amount FROM ( SELECT ious, DATE_FORMAT(transfer, '%Y%m%d') AS transfer, hand_over_agecd, IF ( hand_over_amount > cur_amount, cur_amount, hand_over_amount) hand_over_amount, cur_amount AS cur_amount FROM outsource_allocation_repayment
1=1
AND COMPANY = #{company}
) r ) z GROUP BY z.transfer, z.hand_over_agecd;
